int jumpSearch(std::vector<int>& arr, int target) {
    int n = arr.size();
    int step = sqrt(n); // Jump step size

    int prev = 0;
    while (arr[std::min(step, n) - 1] < target) {
        prev = step;
        step += sqrt(n);
        if (prev >= n)
            return -1; // Element not found
    }

    while (arr[prev] < target) {
        prev++;
        if (prev == std::min(step, n))
            return -1; // Element not found
    }

    if (arr[prev] == target)
        return prev;

    return -1; // Element not found
}

